import React, { useState,useEffect } from 'react';
import { Route, Routes } from "react-router-dom";
import Header from './components/Layout/Header';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import Home from './components/Layout/Home';
import WishList from './components/Cart/WishList';
import LoginForm from './components/Layout/LoginForm';
import Checkout from './components/Cart/Checkout';
import SuccessModal from './components/Cart/SuccessModal';
import "../src/apidata.css"
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  
  const [myData,setmyData] = useState([])


useEffect(() => {
  axios.get("https://fakestoreapi.com/products")
  // axios.get("https://jsonplaceholder.typicode.com/posts")
  .then((res) =>setmyData(res.data)
    // console.log(res.data)
  )
},[])

  return (
    <CartProvider>
      <Header isLoggedIn = { isLoggedIn } setIsLoggedIn = {setIsLoggedIn} />
      <Routes>
          <Route path="/" element={<Home/>} />
          <Route path="/login" element={<LoginForm isLoggedIn = { isLoggedIn }setIsLoggedIn = { setIsLoggedIn }/>} />
          <Route path="/wishlist" element={<WishList/>} />
          <Route path="/cart" element={<Cart isLoggedIn = { isLoggedIn } />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/success" element={<SuccessModal />} />

       


      </Routes>
      <div className='apidata'>
      <h1>Fetch The Data</h1>
      <button> Mock Server Api Call with Json Server </button>
      {myData.map((post)=> {
        const {id,image, title, description,price,category}= post
        return(
          <div>
          <h1>{category}</h1>
          <h3>{id}</h3>
          <h2>{title}</h2>
          <h5>{price }</h5>
          <h3>{description}</h3>
          <h4>{image}</h4>

          </div>
        )
      })}
      </div>
    </CartProvider>
    
  );
}
        
export default App
