// import Cards from './Cards'; // Import Cards component to display individual product cards

const AvailableItems = ({ category }) => { 
    let filteredProducts = TOP_DEALS; // Initialize filteredProducts with all available products

    // If a specific category is selected (not "All"), filter products accordingly
    if (category !== "All") { 
        filteredProducts = TOP_DEALS.filter((product) => product.category === category);
    }

    return (
        <div className='App'> {/* Container div to hold all product cards */}
            {filteredProducts.map(item => ( // Loop through filtered products and render a Cards component for each
                <Cards 
                    id={item.id} // Unique product ID
                    key={item.id} // React key for list rendering optimization
                    name={item.name} // Product name
                    description={item.description} // Product description
                    price={item.price} // Product price
                    image={item.image} // Product image URL
                />
            ))}
        </div>
    );
};

export default AvailableItems; // Export AvailableItems component to be used elsewhere in the application
